/*******************************/


.question-content {
    opacity: 0;
    height: 0px;
    overflow: hidden;
    line-height: 0px;
    padding: 0 0 0 50px;
    border: 0px solid transparent;
    transition: 0.25s ease-in-out all;
}

.faq-question .question-heading {
    margin: 0px;
    font-size: 24px;
    font-weight: 300;
}

.faq-question {
  border: 2px solid whitesmoke;
  margin: 10px 0;
}

.faq-question .question-heading {
    padding: 5px 10px 5px 50px;
    position: relative;
    cursor: pointer;
    display: block;
  
}

.icon:before {
    content: "";
    width: 20px;
    height: 2px;
    background: #ffffff;
    position: absolute;
    top: 14px;
    left: 5px;
    transition: 0.35s ease-in-out all;
}

.icon:after {
    content: "";
    width: 2px;
    height: 20px;
    background: #ffffff;
    position: absolute;
    top: 5px;
    left: 14px;
    transition: 0.35s ease-in-out all;
}


.faq-question:hover {
    border: 2px solid gray;
}

.faq-question .icon {
    pointer-events: none;
    position: absolute;
    left: 2.5px;
    top: 4px;
    width: 30px;
    height: 30px;
    background: #3a3e77;
}

.faq-question.open-faq {
  background: #ececec;    
}

.faq-question.open-faq .question-content {
    padding: 10px 10px 10px 50px;
    height: inherit;
    overflow: hidden;
    line-height: 1em;
    opacity: 1;
}

.faq-question.open-faq:hover {
    border: 2px solid transparent;
}

.faq-question.open-faq .question-heading .icon:before{
    transform: rotate(180deg);
}

.faq-question.open-faq .question-heading .icon:after{
    transform: rotate(270deg);
}

.question-content {
    display: block;
    opacity: 0;
}

.open-faq .question-content {
    opacity: 1;
}


.content-faq nav ul li a:before {
    content: "";
    width: 5px;
    height: 5px;
    background: white;
    display: block;
    position: absolute;
    left: 5px;
    top: 15px;
    border-radius: 50%;
    transition: 0.25s ease-in-out all;
}


.content-faq nav ul li a.current:before {
    left: 0px;
    top: 0px;
    width: 2px;
    border-right: 3px solid rgba(0,0,0,0.15);
    height: 100%;
    border-radius: 0px;
    background: rgba(0,0,0,0.15);
}

.content-faq nav ul li a:after {
    content: "";
    width: 0px;
    height: 100%;
    background: rgba(255,255,255,0.5);
    display: block;
    position: absolute;
    left: 0px;
    top: 0px;
    border-radius: 0 50% 50% 0;
    transition: 0.15s ease-in-out all;
}

.content-faq nav ul li a.current:after {
    width: 100%;
    border-right: none;
    height: 100%;
    border-radius: 0px;
    z-index: -1;
    background: rgba(0,0,0,0.15);
    transition: 0.5s ease-in-out all;
}



