<script>
    
mainNavLinks = typeof(mainNavLinks) == 'undefined' ? document.querySelectorAll(".content-faq nav ul li a") : mainNavLinks;

if (typeof mainSections == 'undefined') {
    let mainSections = document.querySelectorAll("main section");
};
if (typeof lastId == 'undefined') {
    let lastId;
};
if (typeof cur == 'undefined') {
    let cur = [];
};

// This should probably be throttled.
// Especially because it triggers during smooth scrolling.
// https://lodash.com/docs/4.17.10#throttle
// You could do like...
// window.addEventListener("scroll", () => {
//    _.throttle(doThatStuff, 100);
// });
// Only not doing it here to keep this Pen dependency-free.

window.addEventListener("scroll", event => {
  let fromTop = (window.scrollY + 120);

  mainNavLinks.forEach(link => {
    let section = document.querySelector(link.hash);

    if (
      section.offsetTop <= fromTop &&
      section.offsetTop + section.offsetHeight > fromTop
    ) {
      link.classList.add("current");
    } else {
      link.classList.remove("current");
    }
  });
});


$(document).ready(function(){
  
  window.addEventListener("scroll", event => {
    let fromTop = (window.scrollY + 20);
    $(".main-page-faq-container sectio").each(function(){
      if (
          this.offsetTop <= fromTop &&
          this.offsetTop + this.offsetHeight > fromTop
        ) {
          link.classList.add("current");
        } else {
          link.classList.remove("current");
        }
        alert($(this).text())
      });
 
  });
  
  $('.clearInputValueFAQ').on('click', function(event){
      event.preventDefault();
      $('#searchFaqSide').val('');
      $('#searchFaqSide').trigger('keyup');
  });
  
  $(".faq-question .question-heading").on('click',function(){
    $(this).parent(".faq-question").toggleClass("open-faq");
  })
  
  $("#searchFaqSide").on("keyup", function() {
    var value = $(this).val().toLowerCase();
    $(".question-heading h4").filter(function() {
      $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
    });
  });
  
  $("#searchFaqSide").keyup(function(){
 
        // Retrieve the input field text and reset the count to zero
        var filter = $(this).val(), count = 0;
 
        // Loop through the comment list
        $("section.faq-section").each(function(){
 
            // If the list item does not contain the text phrase fade it out
            if ($(this).text().search(new RegExp(filter, "i")) < 0) {
                $(this).fadeOut();
  
            // Show the list item if the phrase matches and increase the count by 1
            } else {
                $(this).show();
                $(this).children(".faq-question").each(function(){

                    // If the list item does not contain the text phrase fade it out
                    if ($(this).text().search(new RegExp(filter, "i")) < 0) {
                        $(this).fadeOut();

                    // Show the list item if the phrase matches and increase the count by 1
                    } else {
                        $(this).show();
                        count++;
                    }
                });
                count++;
            }
        });
      window.scrollTo(0, document.getElementById("main-sections-faq").offsetTop);
    });
})


</script>